
public class Second {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 34;
		int j = 56;
		int wholeNumber = i * j + 56;
		System.out.println(wholeNumber);
		double d = 45.78;
		double decimal = d / 2.23;
		System.out.println(decimal);
		System.out.println(wholeNumber * decimal);
		int chop = (int) decimal;		// chop off the decimal
		System.out.println(chop);

	}

}
